import React from 'react';
import { View, Text, Button, StyleSheet, Linking } from 'react-native';

export default function PaymentScreen({ navigation }) {
  const stripeLink = 'https://buy.stripe.com/test_placeholder'; // Replace this with your real Stripe link

  return (
    <View style={styles.container}>
      <Text style={styles.title}>Payment</Text>
      <Text style={styles.info}>Tap below to pay securely via Stripe:</Text>
      <Button title="Pay Now" onPress={() => Linking.openURL(stripeLink)} color="#4CAF50" />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    padding: 20,
    backgroundColor: '#F5F5F5',
  },
  title: {
    fontSize: 24,
    marginBottom: 20,
    fontWeight: 'bold',
    color: '#4CAF50',
  },
  info: {
    fontSize: 16,
    marginBottom: 20,
    textAlign: 'center',
  },
});
