import React, { useState } from 'react';
import {
  View,
  Text,
  Button,
  StyleSheet,
  TextInput,
  ScrollView,
  Switch
} from 'react-native';
import { Calendar } from 'react-native-calendars';

export default function BookingScreen({ navigation }) {
  const [services, setServices] = useState({
    mowing: false,
    edging: false,
    trimming: false,
    cleanup: false,
  });

  const [selectedDate, setSelectedDate] = useState('');
  const [address, setAddress] = useState('');
  const [notes, setNotes] = useState('');

  const toggleService = (service) => {
    setServices({ ...services, [service]: !services[service] });
  };

  // âœ… Set calendar limits from today to 30 days out
  const today = new Date();
  const formattedToday = today.toISOString().split('T')[0];

  const futureDate = new Date(today);
  futureDate.setDate(futureDate.getDate() + 30);
  const formattedMax = futureDate.toISOString().split('T')[0];

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.title}>Book Your Lawn Service</Text>

      {Object.keys(services).map((service) => (
        <View key={service} style={styles.switchRow}>
          <Text style={styles.label}>
            {service.charAt(0).toUpperCase() + service.slice(1)}
          </Text>
          <Switch
            value={services[service]}
            onValueChange={() => toggleService(service)}
          />
        </View>
      ))}

      <Text style={styles.label}>Select Date</Text>
      <Calendar
        onDayPress={(day) => setSelectedDate(day.dateString)}
        markedDates={{
          [selectedDate]: {
            selected: true,
            selectedColor: '#4CAF50',
          },
        }}
        minDate={formattedToday}
        maxDate={formattedMax}
      />

      <TextInput
        placeholder="Service Address"
        style={styles.input}
        value={address}
        onChangeText={setAddress}
      />

      <TextInput
        placeholder="Additional Notes"
        style={styles.input}
        value={notes}
        onChangeText={setNotes}
        multiline
        numberOfLines={4}
      />

      <Button
        title="Continue to Payment"
        onPress={() => navigation.navigate('Pay')}
        color="#4CAF50"
      />
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    padding: 20,
    backgroundColor: '#F5F5F5',
    flexGrow: 1,
  },
  title: {
    fontSize: 24,
    marginBottom: 20,
    fontWeight: 'bold',
    color: '#4CAF50',
  },
  input: {
    padding: 12,
    borderColor: '#CCC',
    borderWidth: 1,
    borderRadius: 6,
    marginBottom: 16,
    backgroundColor: '#FFF',
  },
  switchRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 12,
  },
  label: {
    fontSize: 16,
    marginBottom: 8,
  },
});
