import React, { useState } from 'react';
import {
  View,
  Text,
  Button,
  StyleSheet,
  TextInput,
  ScrollView,
  Switch,
  TouchableOpacity,
  Platform
} from 'react-native';
import DateTimePicker from '@react-native-community/datetimepicker';

export default function BookingScreen({ navigation }) {
  const [services, setServices] = useState({
    mowing: false,
    edging: false,
    trimming: false,
    cleanup: false,
  });

  const [selectedDate, setSelectedDate] = useState(new Date());
  const [showDatePicker, setShowDatePicker] = useState(false);
  const [address, setAddress] = useState('');
  const [notes, setNotes] = useState('');

  const toggleService = (service) => {
    setServices({ ...services, [service]: !services[service] });
  };

  // ✅ Fix: Zero out the time to allow full day selection
  const today = new Date();
  today.setHours(0, 0, 0, 0);

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <Text style={styles.title}>Book Your Lawn Service</Text>

      {Object.keys(services).map((service) => (
        <View key={service} style={styles.switchRow}>
          <Text style={styles.label}>{service.charAt(0).toUpperCase() + service.slice(1)}</Text>
          <Switch
            value={services[service]}
            onValueChange={() => toggleService(service)}
          />
        </View>
      ))}

      <TouchableOpacity onPress={() => setShowDatePicker(true)} style={styles.input}>
        <Text>{selectedDate.toDateString()}</Text>
      </TouchableOpacity>

      {showDatePicker && (
        <DateTimePicker
          value={selectedDate}
          mode="date"
          display={Platform.OS === 'ios' ? 'spinner' : 'default'}
          minimumDate={today} // ✅ Fixed to allow future days
          onChange={(event, date) => {
            setShowDatePicker(false);
            if (date) {
              setSelectedDate(date);
            }
          }}
        />
      )}

      <TextInput
        placeholder="Service Address"
        style={styles.input}
        value={address}
        onChangeText={setAddress}
      />
      <TextInput
        placeholder="Additional Notes"
        style={styles.input}
        value={notes}
        onChangeText={setNotes}
        multiline
        numberOfLines={4}
      />

      <Button
        title="Continue to Payment"
        onPress={() => navigation.navigate('Pay')}
        color="#4CAF50"
      />
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    padding: 20,
    backgroundColor: '#F5F5F5',
    flexGrow: 1,
  },
  title: {
    fontSize: 24,
    marginBottom: 20,
    fontWeight: 'bold',
    color: '#4CAF50',
  },
  input: {
    padding: 12,
    borderColor: '#CCC',
    borderWidth: 1,
    borderRadius: 6,
    marginBottom: 16,
    backgroundColor: '#FFF',
  },
  switchRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 12,
  },
  label: {
    fontSize: 16,
  },
});
